import { useEffect, useRef } from 'react';
import s from './style.module.css'

export function UserRelation({ text }: { text: string }) {

	const divRef = useRef(null);

	useEffect(() => {
	  if (divRef.current) {
		divRef.current.style.width = `${divRef.current.scrollWidth}px`;
	  }
	}, [text]);

	return (
		<div className={s.container} ref={divRef}>
			<p>You like this user ahahahh</p>
		</div>
	);
}
