import { useEffect, useState } from 'react';
import { SideMenu } from '../../components/SideMenu/SideMenu';
import { Header } from '../../components/Header/Header';
import { Outlet, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { RootState } from '../../store';
import { getCookieByName, getToken } from '../../utils/auth';
import { BackApi } from '../../api/back';
import s from './style.module.css'
import { useDispatch } from 'react-redux';
import { saveAvatar, saveFirstName, saveId, saveSection } from '../../store/user/user-slice';

export function Apps() {

	const navigate = useNavigate();
	const selector = useSelector((store: RootState) => store.user.user);
	const dispatch = useDispatch();

	function updateSection (newSection: string){
		dispatch(saveSection(newSection));
	};

	async function checkToken() {
		const token: string | null = getCookieByName('token');
		if (!token) {
			navigate('/signin');
		}
		else {
			const rep = await BackApi.checkToken(token);
			if (rep.status !== 200) {
				navigate('/signin');
			}
			if (selector.id === 0) {
				dispatch(saveId(rep.data.userId));
			}
		}
	}

	async function getInfoUser() {
		const token = getToken();
		if (token) {
			const response = await BackApi.getUserById(selector.id, token);
			dispatch(saveAvatar(response.data.mainPhoto));
			dispatch(saveFirstName(response.data.firstName));
		} else {
			navigate('/signin');
		}
	}

	function geoLocation() {
		return new Promise((resolve, reject) => {
		  // Vérifier si la géolocalisation est disponible dans le navigateur
		  if ("geolocation" in navigator) {
			// Demander la localisation de l'utilisateur
			navigator.geolocation.getCurrentPosition(
			  (position) => {
				const latitude = position.coords.latitude;
				const longitude = position.coords.longitude;
				resolve({ latitude, longitude });
			  },
			  (error) => {
				reject(new Error("Erreur lors de l'obtention de la localisation :" + error.message));
			  }
			);
		  } else {
			reject(new Error("La géolocalisation n'est pas disponible dans ce navigateur."));
		  }
		});
	  }

	async function getUserLocation() {
		let location
		try {
			const location: any = await geoLocation();
			// console.log("Latitude :", location.latitude);
			// console.log("Longitude :", location.longitude);
		} catch (error) {
			try {
				const rep = await BackApi.getIpInfo();
				console.log('rep.data', rep.data.loc);
			} catch (ipError: any) {
				console.error('Erreur lors de la récupération des informations basées sur l\'adresse IP :', ipError.message);
			}
		}
	}


	// function location() {
	// 	// Vérifier si la géolocalisation est disponible dans le navigateur
	// 	if ("geolocation" in navigator) {
	// 		// Demander la localisation de l'utilisateur
	// 		navigator.geolocation.getCurrentPosition(
	// 			(position) => {
	// 				const latitude = position.coords.latitude;
	// 				const longitude = position.coords.longitude;
	// 				console.log("Latitude :", latitude);
	// 				console.log("Longitude :", longitude);
	// 				// Vous pouvez utiliser la latitude et la longitude ici pour effectuer d'autres actions
	// 			},
	// 			(error) => {
	// 				console.error("Erreur lors de l'obtention de la localisation :", error.message);
	// 			}
	// 		);
	// 	} else {
	// 		console.error("La géolocalisation n'est pas disponible dans ce navigateur.");
	// 	}




		// const test = await BackApi.test();
		// console.log('test', test.data);




		// navigator.geolocation.getCurrentPosition( result=>{
		// 	// Open the map location using the lat and lng.
		// 	 window.open(`https://maps.google.com/?${result.coords.latitude},${result.coords.longitude}`)
		//   }, error=>{
		// 	console.log("Error", error)
		//   })
	// }

	useEffect(() => {
		checkToken();
		if (selector.id !== 0) {
			getInfoUser();
			getUserLocation();
		}
		// eslint-disable-next-line
	}, [selector.section, selector.id])

	return (
		<div className={s.app}>
			<SideMenu section={selector.section} updateSection={updateSection}/>
			<div className={s.content}>
				<Header section={selector.section} />
				<Outlet />
			</div>
		</div>
	);
}
