import React, { useRef, useEffect } from 'react';

function MyComponent({ text }) {
  const divRef = useRef(null);

  useEffect(() => {
    if (divRef.current) {
      const width = divRef.current.offsetWidth;
      divRef.current.style.width = `${width}px`;
    }
  }, [text]);

  return (
    <div className={s.container} ref={divRef}>
      {text}
    </div>
  );
}
