import { useState } from 'react';
import filter from '../../assets/filters.svg'
import { LargeCardUser } from '../../components/LargeCardUser/LargeCardUser';
import s from './style.module.css'

export function Search() {
	const [isClicked, setIsClicked] = useState<boolean>(true);
	const [distanceValue, setDistanceValue] = useState<number>(1);
	const [popularityValue, setPopularityValue] = useState<number>(1);

	const handleDistanceChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		setDistanceValue(Number(event.target.value));
	};

	return (
		<div className={s.container}>
			<div className={s.button}>
				<div className={s.filter}>
					<img src={filter} alt='filter' onClick={() => setIsClicked(!isClicked)} />
				</div>
			</div>
			<div
				className={s.boxFilters}
				style={{display: isClicked ? 'flex' : 'none'}}
			>
				<div className={s.boxTitle}><span className={s.title}>Search filters</span></div>
				<div className={s.allFilters}>
					<div className={s.filterA}>
						<div className={s.sliderDistance}>
							<label htmlFor="volume">Maximum distance {distanceValue}</label>
							<input type="range" id="distance" name="distance" min="1" max="50" value={distanceValue} onChange={handleDistanceChange}/>
							<label htmlFor="distance">Maximum popularity difference {popularityValue}</label>
							<input type="range" id="popularity" name="popularity" min="0" max="11" />
						</div>
					</div>
					<div className={s.filterB}>B</div>
				</div>
				<div className={s.buttonSubmit}><button className={s.buttonFilters}>Submit</button></div>
			</div>
			<div
				className={s.users}
				style={{ opacity: isClicked ? '0.3' : '1' }}
			>
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
				<LargeCardUser />
			</div>
		</div>
	);
}
