import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { RootState } from '../../store'
import logo from '../../assets/notification.svg'
import logoUser from '../../assets/testUser.png'
import s from './style.module.css'
import { BackApi } from '../../api/back'
import { getToken } from '../../utils/auth'
import { useNavigate } from 'react-router-dom'
import { useDispatch } from 'react-redux'

interface HeaderProps {
	section: string
}

export function Header({ section }: HeaderProps) {

	const selector = useSelector((store: RootState) => store.user.user);
	const dispatch = useDispatch();
	const navigate = useNavigate();
	const [user, setUser] = useState<any>(null);
	const [profilePicture, setProfilePicture] = useState<any>(null);

	async function getInfoUser() {
		const token = getToken();
		if (token) {
			const response = await BackApi.getUserById(selector.id, token);
			setUser(response.data);
			setProfilePicture(response.data.mainPhoto);
			dispatch();
		} else {
			navigate('/signin');
		}
	}

	useEffect(() => {
		if (selector.id !== 0) {
			getInfoUser();
		}
	}, [selector.id])

	if (selector.id === 0 || !user || !profilePicture) {
		return (
			<>
			</>
		);
	}

	return (
		<header className={s.header}>
			<div className={s.section}>
				{section}
			</div>
			<div className={s.user}>
				<div className={s.notification}>
					<img className={s.logoBell} src={logo} alt='notification'/>
				</div>
				<div className={s.infoUser}>
					{/* <img className={s.logoUser} src={logoUser} alt='logoUser'/> */}
					<img className={s.logoUser} src={`data:image/jpeg;base64,${profilePicture}`} alt='userAvatar' />
					{/* Jean-Baptiste */}
					{user.firstName}
				</div>
			</div>
		</header>
	);
}
