import logo from '../../assets/logo.png'
import s from './style.module.css'
import { InputLogin } from '../../components/InputLogin/InputLogin';
import { ButtonLogin } from '../../components/ButtonLogin/ButtonLogin';
import { FormEvent, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { BackApi } from '../../api/back';
import { createCookie, parseJwt } from '../../utils/auth';
import { useDispatch } from 'react-redux';
import { saveId } from '../../store/user/user-slice';

export function Signin() {

	const dispatch = useDispatch();
	const navigate = useNavigate();
	const [backErr, setBackErr] = useState<string>('');

	async function handleSubmit(e: FormEvent<HTMLFormElement>) {
		e.preventDefault();
		const formData = new FormData(e.currentTarget);
		const obj = Object.fromEntries(formData);
		const rep = await BackApi.signin(obj);
		if (rep.status === 200) {
			const id = parseJwt(rep.data.token).userId;
			createCookie("token", rep.data.token);
			dispatch(saveId(id));
			const response = await BackApi.getUserById(id, rep.data.token);
			console.log('VERIFIED');
			if (response.status === 200) {
				if (!response.data.all_info_set) {
					navigate('/age');
				} else if (!response.data.verified) {
					navigate('/verifiedAccount');
				} else {
					navigate('/search');
				}
			}
		} else {
			setBackErr(rep);
		}
	}

	return (
		<div className={s.container}>
			<div className={s.bgSignupBox}>
				<div className={s.signupBox}>
					<div className={s.name}>
						<img className={s.image} src={logo} alt='logo' />
						Join Flirtopia now
					</div>
					<form onSubmit={handleSubmit}>
						<div className={s.form}>
							<div className={s.input}>
								<InputLogin
									label='Username'
									name='username'
									placeholder='Enter your username'
									small={false}
								/>
								<InputLogin
									label='Password'
									name='password'
									placeholder='Enter your password'
									small={false}
									isPassword={true}
								/>
								<div
									className={s.forgot}
									onClick={() => navigate('/forgotPassword')}
								>
									Forgot password ?
								</div>
							</div>
							{backErr && <span className={s.error}>{backErr}</span>}
							<div className={s.space}>
								<div className={s.button}>
									<ButtonLogin
									name={'Signin'}
									small={false}
									colorPink={true}
									isSubmit={true}
								/>
								</div>
								<ButtonLogin
									name={'Signup'}
									small={false}
									colorPink={false}
									isSubmit={false}
									onClick={() => navigate('/signup')}
								/>
							</div>
						</div>
					</form>
				</div>
			</div>
		</div>
	);
}
