		// 	// Demander la localisation de l'utilisateur
		// 	navigator.geolocation.getCurrentPosition(
		// 		(position) => {
		// 			const latitude = position.coords.latitude;
		// 			const longitude = position.coords.longitude;
		// 			console.log("Latitude :", latitude);
		// 			console.log("Longitude :", longitude);
		// 			// Vous pouvez utiliser la latitude et la longitude ici pour effectuer d'autres actions
		// 		},
		// 		(error) => {
		// 			console.error("Erreur lors de l'obtention de la localisation :", error.message);
		// 		}
		// 	);
		// } else {
		// 	console.error("La géolocalisation n'est pas disponible dans ce navigateur.");
		// }










		// navigator.geolocation.getCurrentPosition( result=>{
		// 	// Open the map location using the lat and lng.
		// 	 window.open(`https://maps.google.com/?${result.coords.latitude},${result.coords.longitude}`)
		//   }, error=>{
		// 	console.log("Error", error)
		//   })
	}

	useEffect(() => {
		checkToken();
		if (selector.id !== 0) {
			getInfoUser();
			location();
		}
		// eslint-disable-next-line
	}, [selector.section, selector.id])

	return (
		<div className={s.app}>
			<SideMenu section={selector.section} updateSection={updateSection}/>
			<div className={s.content}>
				<Header section={selector.section} />
				<Outlet />
			</div>
		</div>
	);
}
