import { useEffect, useState } from "react";
import { Outlet, useNavigate } from 'react-router-dom';
import { BackApi } from "../../api/back";
import { getCookieByName, parseJwt } from "../../utils/auth";
import { useSelector } from "react-redux";
import { RootState } from "../../store";
import { useDispatch } from "react-redux";
import { saveInfoUser } from "../../store/user/user-slice";

export function UserInfo() {

	const navigate = useNavigate();
	const dispatch = useDispatch();
	const selector = useSelector((store: RootState) => store.user.user);
	const [jwt, setJwt] = useState<string | null>(null);

	console.log('TEST selector.id', selector.id);

	async function checkToken() {
		const token: string | null = getCookieByName('token');
		// console.log('TOKEN =', token);
		if (!token) {
			navigate('/signin');
		} else {
			const rep = await BackApi.checkToken(token);
			if (rep.status !== 200) {
				// const response = await BackApi.getUserById(selector.id);
				// console.log('NOP');
				navigate('/signin');
			} else {
				let id;
				if (selector.id === 0) {
					const id = parseJwt(token).userId;
					// console.log('selector.id', selector.id);
					dispatch(saveInfoUser(id));
					// console.log('selector.id', selector.id);
				}
				setJwt(token);
				const response = await BackApi.getUserById(selector.id, token);
				if (response.status === 200) {
					const user = response.data;
					// console.log(typeof user.birth);
					if (user.birth === null) {
						 navigate('/age');
					}
					else if (!user.gender) {
						 navigate('/gender');
					}
					else if (!user.preference) {
						 navigate('/preference');
					}
					else if (!user.description) {
						navigate('/description');
					}
					else if (!user.interests) {
						navigate('/interests');
					}
					else if (!user.photo1) {
						navigate('/mainPhoto');
					}
				}

			}
		}
	}

	useEffect(() => {
		checkToken();
	}, [])

	if (!jwt) {
		return (
			<>
			</>
		);
	}

	return (
		<>
			<Outlet />
		</>
	);
}
