import axios from "axios";

const BASE_URL = 'http://localhost:3000';

export class BackApi {
	static async getAllUsers() {
		const rep = await axios.get(`${BASE_URL}/users`)
			.then(rep => rep)
			.catch(error => error)
		return rep;
	}

	// static async createUser(infoUser: any, {

	// }) {
	// 	const rep = await axios.post(`${BASE_URL}/user`)
	// 		.then (rep => rep)
	// 		.catch (error => error)
	// 	return rep;
	// }

	static async createUser(infoUser: any) {
		try {
			const response = await axios.post(`${BASE_URL}/user`, infoUser);
			return response.data;
		} catch (error) {
			console.error('Erreur lors de la création de l\'utilisateur :', error);
			throw error;
		}
	}
}
